import {I} from 'test-maker';

export class PegaVersion {

    private static instance: PegaVersion;

    constructor() {
        if (PegaVersion.instance) {
            throw new Error("Error - use Singleton.getInstance()");
        }
    }

    static getInstance(): PegaVersion {
        PegaVersion.instance = PegaVersion.instance || new PegaVersion();
        return PegaVersion.instance;
    }

    _version: number;

    public async setVersion(): Promise<void> {
        //@ts-ignore
        let currentVersion = await I.evalInCurrentWindowMainFrame(() => pega.desktop.pxPdnURI);
        currentVersion = currentVersion.substring(currentVersion.length-3);
        //console.log('Current Pega version is ' + currentVersion);
        this._version = currentVersion;
    }

    public getVersion(): number {
        return +this._version;
    }
}

export const pegaVersion = new PegaVersion();




