import {TextInputCommon} from '../textInput/textInputCommon';
import {DropdownCommon} from './dropdownCommon';

export class DropdownByLabel extends DropdownCommon {

    constructor(label: string, container: string = '') {
        super (`${container}//label[contains(text(), "${label}") and not(contains(@for, "${label}"))]/following-sibling::div/select[@data-ctl='["Dropdown"]']`);
    }
}

export function dropdownByLabel(label, container?) {
    return new DropdownByLabel(label, container);
}