import {TextInputCommon} from './textInputCommon';

export class TextInputByLabel extends TextInputCommon {

    constructor(label: string, container: string = '') {
        super (`${container}//label[contains(text(), "${label}") and not(contains(@for, "${label}"))]/following-sibling::div//input[@data-ctl='["TextInput"]']`);
    }
}

export function textInputByLabel(label, container?) {
    return new TextInputByLabel(label, container);
}