import {TextInputCommon} from '../textInput/textInputCommon';
import {TextAreaCommon} from './textAreaCommon';

export class TextAreaByAttribute extends TextAreaCommon {

    constructor(attributeName: string, attributeValue: string, container: string = '') {
        super (`${container}//textarea[contains (@${attributeName}, "${attributeValue}")]`);
    }
}

export function textAreaByAttribute(attributeName, attributeValue, container?) {
    return new TextAreaByAttribute(attributeName, attributeValue, container);
}