export function getUsersPool(env: string) {
    //console.log(env);
    switch (env) {
        case `dev`:
            users = dev_users;
            break;
        case `stage`:
            users = staging_users;
            break;
        default:
            users = [
                {
                    id: `operator`,
                    alt: [{ username: `test.maker`, password: `Rules12345$` }],
                },
            ];
            break;
    }

    //console.log(users);
    return users;
}

export function getUrl(env: string) {
    let url = '';
    switch (env) {
        case `dev`:
            url = 'https://dev.k-expert.com:8084/prweb/';
            break;
        case `stage`:
            url = 'https://dev.k-expert.com:8085/prweb/';
            break;
        default:
            url = 'https://dev.k-expert.com:8084/prweb/';
            break;
    }
    return url;
}

