export interface Build {
    compiler: {
        /**
         * @default false
         */
        compileOnly: boolean;
        /**
         * @default true
         */
        failOnError: boolean;
        /**
         * @default false
         */
        failOnWarning: boolean;
        typescript: {
            /**
             * @default false
             */
            typeCheck: boolean;
        };
    };
    cleanUp: {
        /**
         * @default true
         */
        reports: boolean;
        /**
         * @default true
         */
        tests: boolean;
    };
    output: {
        /**
         * @default `${projectRoot}/dist`
         */
        path: string;
    };
}
