export interface Gherkin {
    parser: {
        /**
         * @default false
         */
        enabled: boolean;
        /**
         * @default `./features`
         */
        jsonPath: string;
        /**
         * @default [`./features/**\/*.feature`]
         */
        source: string[];
        /**
         * @default /^{{\s*\/
         */
        argumentStartDelimiter: RegExp;
        /**
         * @default /^\s*}}/
        */
        argumentEndDelimiter: RegExp;
    };
}
