import { TestMakerReporterOptions } from "../test-maker-reporter-options";
export interface AllureReporterOptions extends TestMakerReporterOptions {
    scoreBase?: `feature` | `scenario`;
    /**
     * @default DD/MM/YYYY
     <pre>
     Month	M	1 2 ... 11 12
     MM	01 02 ... 11 12
     MMM	Jan Feb ... Nov Dec
     MMMM	January February ... November December
     Day of Month	D	1 2 ... 30 31
     Do	1st 2nd ... 30th 31st
     DD	01 02 ... 30 31
     Day of Week	d	0 1 ... 5 6
     ddd	Sun Mon ... Fri Sat
     dddd	Sunday Monday ... Friday Saturday
     Year	YY	70 71 ... 29 30
     YYYY	1970 1971 ... 2029 2030
     </pre>
     */
    dateFormat?: string;
    resultsFolder?: string;
    reportFolder?: string;
    packResultsFiles?: string;
    packReportFiles?: string;
    generateReport?: boolean;
    keepHistory?: boolean;
    historyFolder?: string;
    open?: boolean;
}
