import { NotRetryableError } from "../error-parser/not-retryable-error";
export interface WaitForConditionOptions {
    condition: boolean | ((notRetryableError?: NotRetryableError) => boolean) | ((notRetryableError?: NotRetryableError) => Promise<boolean>);
    interval?: number;
    timeout?: number;
    retries?: number | `INFINITELY`;
    backoff?: "FIXED" | "EXPONENTIAL" | "LINEAR" | ((attempt: number, delay: number) => number);
    throwOnError?: boolean;
    maxBackOff?: number;
    errorMessage?: string;
    overrideErrorMessage?: string;
    retryMessage?: string | ((currentRetry: number) => Promise<string> | Promise<void>);
}
